\label{thm:bohsetdef}
	For a set of characters $\Gamma \subset \wh{G}$ and
	a real number $\delta > 0$, we let
	\begin{align*}
		B(\Gamma,\delta) = \{ x \in G \,:\, |1-\gamma(x)| 
		\leq \delta \ \ \forall \gamma \in \Gamma \}
	\end{align*}
	be the Bohr set of \textit{frequency set} $\Gamma$ and 
	\textit{radius} $\delta$.
	We define $d=|\Gamma|$ to be the \textit{dimension} of this Bohr set.

\label{def.vbr}
The \emph{Bollob\'as-Riordan polynomial} , $R_{ ( G,\mathcal{P})}(x,y,z)$, of a vertex partitioned ribbon graph  $( G,\mathcal{P})$ is defined by 
\begin{align*} R_{ ( G,\mathcal{P})}(x,y,z) &=    \alpha( 1,\mathbf{x}, \mathbf{y}) ( G,\mathcal{P})  
\\ &= \sum_{A\subseteq E(G)}  (x-1)^{r(G_{/\mathcal{P}}) -  r(A_{/\mathcal{P}})}  y^{|A|-r(A_{/\mathcal{P}})} z^{2( \rho(A)-r(A_{/\mathcal{P}}) )},
\end{align*}
where $  \alpha$ is as in Theorem~\ref{t.vbr1} and   $\mathbf{x} = ( 1, y,yz,yz^2)$, $\mathbf{y}  =(x-1,1,1,1)$

\label{def:BIP}
Let $u,v \in S_n$ such that $ u \leq v $ in (strong) Bruhat
order.  
The \emph{Bruhat interval polytope}
$\mathsf{Q}_{u,v}$ is defined as the convex hull
$$\mathsf{Q}_{u,v}
= \conv\{(z(1),\dots,z(n)) \ \vert \ \text{ for }z\in S_n \text{ such that }
u \leq z \leq v\}.$$

\label{def:CartesianProduct}
Let $G_1 = (V(G_1),E(G_1))$ and $G_2 = (V(G_2),E(G_2))$ be two graphs. The \emph{Cartesian product} $G_1 \times G_2$ (sometimes in the literature also denoted $G_1 \square G_2$) is the graph with vertex set $V(G_1) \times V(G_2)$, and vertices $(u_1,v_1)$ and $(u_2,v_2)$ are connected by an edge if  
\begin{itemize}
\item either $u_1 = u_2$ and $\{v_1,v_2 \} \in E(G_2)$,
\item or if $v_1 = v_2$ and $\{u_1, u_2 \} \in E(G_1)$.
\end{itemize}

Given two graphs $G$ and $H$, their Cartesian product $G \cart H$  is defined as a graph with the
vertex set $V(G) \times V(H)$ and the following set of edges,
%
\begin{align*}
E(G \cart H) = \inbraces{
\inparen{(i_1,i_2),(j_1,j_2)}  \suchthat 
\insquare{\inparen{(i_1,j_1) \in E(G)} \wedge (i_2 = j_2) } \bigvee
\insquare{(i_1 = j_1) \wedge \inparen{(i_2,j_2) \in E(H)} } 
}\ .
\end{align*}
For a graph $G$, we define $G^{\cart 1} = G$ and $G^{\cart k} = G^{\cart (k-1)} \cart G$.

The \emph{Cartesian product} of graphs $G$ and $H$ is the graph $G \Box H$ such that:
\begin{enumerate}
\item The vertex set of $G\Box H$ is the Cartesian product $V(G) \times V(H)$.
\item Two vertices $(u,u')$ and $(v,v')$ are adjacent in $G\Box H$ if and only if
\begin{itemize}
\item $u = v$ and $u'$ is adjacent to $v'$ in $H$, or
\item $u' = v'$ and $u$ is adjacent to $v$ in $G$.
\end{itemize}
\end{enumerate}

Let $G$ and $H$ be two disjoint graphs. The \textit{Cartesian product} of $G$ and $H$, denoted $G\square H$, is defined by $V(G\square H)= V(G) \times V(H)$ and edges given by:
\begin{enumerate}
\item $(x,y)$ is adjacent to $(w,z)$ if $x=w$ and $y$ and $z$ are adjacent in $H$, or
\item $(x,y)$ is adjacent to $(w,z)$ if $y=z$ and $x$ and $w$ are adjacent in $G$.
\end{enumerate}
where $x,w \in V(G)$ and $y,z\in V(H)$.

The \emph{Cheeger constant} of a graph $G$, denoted $\Phi(G)$, is 
\begin{equation}\label{eq:cheeger}
\Phi(G) = \min_{\substack{ X \subseteq V(G) \\ \vol(X) \leq \frac{1}{2} \vol(V)}} \frac{|E(X,V\setminus X)|}{\vol(X)},
\end{equation}
where $E(X,Y)$ is the set of edges joining $X$ and $Y$ and $\vol(X)=\sum_{v\in X}d(v)$.

