\item\label{item(i):equivweight} The weighted probability of any path in $\mathbb{N}^n$ starting at the origin is the same under $(a_{\bss})$ and $(a'_{\bss})$;
\item\label{item(ii):equivweight} There exist constants $\alpha_1,\dots,\alpha_n,$ and $\beta$ such that the weight assigned to each step $\bss \in \mS$ has the form   $a_{\bss} = a'_{\bss} \cdot \beta \, \prod_{j=1}^n \alpha_j^{\pi_j(\bss)}$;
\item\label{item(iii):equivweight} If $(p_{\bss},p'_{\bss})_{\bss \in \mS \setminus \mT}$ denote pairs of paths satisfying Proposition~\ref{prop:p1p2}, then for every $\bss \in \mS \setminus \mT$,
\begin{equation}
     \prod_{\br \in p_{\bss}} \frac{a_{\br}}{a'_{\br}} = \prod_{\br' \in p'_{\bss}} \frac{a_{\br'}}{a'_{\br'}}, \label{eq:equivweight}
\end{equation}
where the steps are considered with multiplicity.
\end{enumerate}
Two weightings of $\mS$ are said to be \emph{equivalent} if they satisfy one of the above statements.
\label{th:equivweight}

