Let $(\tilde{B},\mathbf{x})$ be a seed in $\cF$, and let $1\leq k \leq n$ be an integer. The \emph{seed mutation} $\mu_k$ in the direction $k$ transforms $(\tilde{B},\mathbf{x})$ into the seed $\mu_k(\tilde{B},\mathbf{x})=(\tilde{B}',\mathbf{x}')$ defined as follows:
\begin{itemize}
\item The entries of $\tilde{B}'=(b'_{ij})$ are given by
\begin{equation}
b'_{ij}=
\begin{cases}
-b_{ij} & \text{if  } i=k\text{ or } j=k; \\
b_{ij}+\text{sgn}(b_{ik})[b_{ik}b_{kj}]_+ & \text{otherwise}.
\end{cases}
\end{equation}
\item The cluster $\mathbf{x}'=\lbrace x'_1,\ldots, x'_n \rbrace$ is given by $x'_j=x_j$ for $j\neq k$, whereas $x'_k\in \cF$ is determined by the \emph{exchange relation}
\begin{equation}
\label{exchange relation}
x'_kx_k=\prod_{i=1}^{2n}x_i^{[b_{ik}]_+}+\prod_{i=1}^{2n}x_i^{[-b_{ik}]_+}
\end{equation}
\end{itemize}

\label{def:seed-mutation}
Let $(\mathbf{x}, \mathbf{y}, B)$ be a labeled seed in $\mathcal{F}$,
and let $k \in [1,N]$.
The \emph{seed mutation} $\mu_k$ in direction~$k$ transforms
$(\mathbf{x}, \mathbf{y}, B)$ into the labeled seed
$\mu_k(\mathbf{x}, \mathbf{y}, B)=(\mathbf{x}', \mathbf{y}', B')$ defined as follows:
\begin{itemize}
\item
The entries of $B'=(b'_{ij})$ are given by
\begin{equation}
\label{eq:matrix-mutation}
b'_{ij} =
\begin{cases}
-b_{ij} & \text{if $i=k$ or $j=k$;} \\[.05in]
b_{ij} + \textup{sgn}(b_{ik}) \ [b_{ik}b_{kj}]_+
 & \text{otherwise.}
\end{cases}
\end{equation}
\item
The coefficient tuple $\mathbf{y}'=(y_1',\dots,y_N')$ is given by
\begin{equation}
\label{eq:y-mutation}
y'_j =
\begin{cases}
y_k^{-1} & \text{if $j = k$};\\[.05in]
y_j y_k^{[b_{kj}]_+}
(y_k \oplus 1)^{- b_{kj}} & \text{if $j \neq k$}.
\end{cases}
\end{equation}
\item
The cluster $\mathbf{x}'=(x_1',\dots,x_N')$ is given by
$x_j'=x_j$ for $j\neq k$,
whereas $x'_k \in \mathcal{F}$ is determined
by the \emph{exchange relation}
\begin{equation}
\label{exchange relation}
x'_k = \frac
{y_k \ \prod x_i^{[b_{ik}]_+}
+ \ \prod x_i^{[-b_{ik}]_+}}{(y_k \oplus 1) x_k} \, .
\end{equation}
\end{itemize}

